    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        jTextArea1.setText("");
        char letraMax = jTextFieldLetraMax.getText().charAt(0);        
        char letra = 'a';
        boolean error = false;
        
        if(letraMax>='A' && letraMax<='Z') {
            letra = 'A';
        } else {
            if(letraMax>='a' && letraMax<='z') {
                letra = 'a';
            } else {
                error = true;
            }
        }
        
        if(!error) {
            while(letra <= letraMax) {
                jTextArea1.append(String.valueOf(letra));
                letra++;
            }
        } else {
            jTextArea1.append("Error");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

